 import { Component, Input } from '@angular/core';
import { CartItem } from '../cart.model';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent {
 @Input() cartItems : CartItem[] = []


 onDelete(i: number) {
this.cartItems.splice(i, 1);
 }
}
<h1> Shopping Cart</h1>


    <div 
    *ngIf="cartItems.length > 0; else ifEmpty">
       <ul *ngFor="let c of cartItems; let i = index">
        <li><img src="/assets/"> Item:{{c.item}}, quantity:{{c.quantity}}
        <button type="button" (click)="onDelete(i)" ></button></li>
       </ul>
    </div>


<ng-template #ifEmpty>
    <p>Your cart is empty</p>
</ng-template>
