import { Component, OnInit, inject } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Friend } from '../models';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent implements OnInit{

  friendsForm!: FormGroup
  factsArray : [] = []
  nameField = 'name'


 //@Autowired
  fb: FormBuilder = inject(FormBuilder)

  //constructor(private fb: FormBuilder) {}


  ngOnInit() {
       this.friendsForm = this.createForm()
   }


   private createForm(): FormGroup {
    return new FormGroup({
      name: new FormControl<string>(''),
      email: new FormControl<string>(''),
      age: new FormControl<number>(10),
      gender: new FormControl<string>('')
    })
   }


   processForm() {
    const friend: Friend = this.friendsForm.value
    console.info('form:' +  friend)
    this.friendsForm.reset()
   }


   invalidField(ctrlName: string): boolean {
    return !!(this.friendsForm.get(ctrlName)?.invalid && this.friendsForm.get(ctrlName)?.dirty)
   }

private createForm2(): FormGroup {

  return this.fb.group({
    [nameField]: this.fb.control<string>('', [ Validators.required, Validators.minLength(3)]),
    email: this.fb.control<string>('', [Validators.required, Validators.email]),
    age: this.fb.control<number>(10, [Validators.min(10)]),
    gender: this.fb.control<string>('')
  })
}

addAFact() {
  this.factsArray.push(this.createFriendsFact());
}

removeFact(){
  this.factsArray.removeAt(1)
}

private createFriendsFact() {
  return this.fb.group({
    fact: new FormControl<string>('', [Validators.minLength(3), Validators.maxLength(16)]),
    value: this.fb.group('', [Validators.minLength(5)])
  })
}


}
