 import { Component, Input } from '@angular/core';
import { Order } from '../order.model';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent {
 @Input() cart: Order[] = [];


}

<div *ngFor="let c of cart">
    <div 
    *ngIf="c; else ifEmpty">
        <p>{{c.name}}</p>
        <p>{{c.quantity}}</p>
    </div>
</div>

<ng-template #ifEmpty>
    <p>Your cart is empty</p>
</ng-template>
