import { Component, ElementRef, ViewChild } from '@angular/core';

@Component({
  selector: 'app-inventory',
  templateUrl: './inventory.component.html',
  styleUrls: ['./inventory.component.css']
})
export class InventoryComponent {
  fruits: string[] = [
    'acorn_squash', 'apple', 'bell_pepper', 'blueberries', 'broccoli',
    'carrot', 'celery', 'chili_pepper', 'corn', 'eggplant', 'harold',
    'lettuce', 'mushroom', 'onion', 'potato', 'pumpkin', 'radish', 'squash',
    'strawberry', 'sugar_snap', 'tomato', 'zucchini' ]

    @ViewChild('nameInput') nameRef : ElementRef;
    @ViewChild('quantityInput') quantity : ElementRef;


    onSelected() {

    }



}

<!--<div *ngFor="let f of fruits">
    <p>{{ f }}</p><span><img src="/assets/{{f}}.png" alt={{ f }}></span>
</div>-->

<div>
    <form *ngFor="let f of fruits" >
        <input type="checkbox" id={{ f }} name= {{ f }} #fruitInput> 
        {{f}} 
        <input type="number" id="quantity" name="quantity" #quantityInput>
        <input type="submit"  (click)="onSelected()"   value="Add">
    </form>
</div>
