import { Component, ElementRef, OnInit, Output } from '@angular/core';
import { Subject } from 'rxjs';
import { CartAction } from '../cart.model';

@Component({
  selector: 'app-inventory',
  templateUrl: './inventory.component.html',
  styleUrls: ['./inventory.component.css']
})
export class InventoryComponent implements OnInit {
  @Output() onItemSelection = new Subject<CartAction>();

  fruits: string[] = [
    'acorn_squash', 'apple', 'bell_pepper', 'blueberries', 'broccoli',
    'carrot', 'celery', 'chili_pepper', 'corn', 'eggplant', 'harold',
    'lettuce', 'mushroom', 'onion', 'potato', 'pumpkin', 'radish', 'squash',
    'strawberry', 'sugar_snap', 'tomato', 'zucchini' ]

   inc( i: number) {
     const action : CartAction = {
item : this.fruits[i],
quantity: 1
     }
     this.onItemSelection.next(action);

   }

   dec(i : number){
    console.log('dec : '+ i);
const action : CartAction = {
  item: this.fruits[i],
  quantity: 1-i
}
this.onItemSelection
   }

    constructor() {

    }

    ngOnInit() {

    }

}
<h2>Inventory</h2>


    <div *ngFor="let f of fruits; let i = index" >
       <img src="/assets/{{f}}.png" width="50">
       {{ f }} uppercase
       <button type="button" (click)="dec(1)">-</button>
       <button type="button" (click)="inc(1)">+</button>
        </div>
