import { Component, Input } from '@angular/core';
import { ActivationEnd } from '@angular/router';
import { CartAction, CartItem } from './cart.model';



@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  cart: CartItem[] = [];

  itemSelected(action: CartAction){
    console.log(action);
    for (let i = 0; i < this.cart.length; i++){
      if (this.cart[i].item == action.item){
        this.cart[i].quantity += action.quantity;
      }
    }

  }


}
<h1>Please select</h1>
<div class="row">
<div class="col-xs-6 pull-left">
    <app-inventory (onItemSelection)="itemSelected($event)"></app-inventory>
</div>
<div class="col-xs-6">
    <app-cart [cartItems]="cart"></app-cart>
</div>
</div>
