import { Component, Input, OnInit, Output, inject } from '@angular/core';
import { FormArray, FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Task } from '../models';
import { Subject } from 'rxjs';

@Component({
  selector: 'app-createtodo',
  templateUrl: './createtodo.component.html',
  styleUrls: ['./createtodo.component.css']
})
export class CreatetodoComponent implements OnInit{
taskForm!: FormGroup
tasksArr!:  FormArray
@Output() onNewTask = new Subject<Task>()



fb: FormBuilder = inject(FormBuilder)


private createTask(): FormGroup {
 return this.fb.group({
  task: this.fb.control<string>(''),
  priority: this.fb.control<string>(''),
  dueDate: this.fb.control<string>('')
 })
}


invalidField(ctrlName: string): boolean {
  return !!(this.taskForm.get(ctrlName)?.invalid && this.tasksForm.get(ctrlName)?.dirty)
}


invalidForm(){
  return this.taskForm.invalid
}

addTask() {
  this.tasksArr.push(this.createTask())
}

removeTask(i: number) {
  this.tasksArray.removeAt(i)
}

processTask() {
  const todo = this.todoForm.value 
}

private createTodo(): FormGroup{
  this.taskArr  = this.fb.array([])
  return this.fb.group({
    title: this.fb.control<string>(''),
    name: this.fb.control<string>(''),
    tasks: this.taskArr
  })
}




ngOnInit() {
this.taskForm = this.createTodo()
}



}
<form [formGroup]="taskForm" (submit)="processTask()">
   <table>
    <tr>
        <td>
            Title
        </td>
        <td>
            <input type="text" size="30" placeholder="Title" formControlName="title">
            <span *ngIf="invalidField('title')">
                Please enter a valid title
            </span>
        </td>
    </tr>
    <tr>
        <td>
            Name
        </td>
        <td>
            <input type="text" size="30" placeholder="Name" formControlName="name">
            <span *ngIf="invalidField('name')">
                Please enter a valid name
            </span>
        </td>
    </tr>
    <tr>
        <td>
            <button type="submit">Save</button>
            <button type="button" (click)="addTask()">Add Task</button>
        </td>
    </tr>
   </table>

  <table *ngIf="tasksArr.length > 0">
    <thead>
        <tr>
            <th>Tasks</th>
            <th>Priority</th>
            <th>Due Date</th>
        </tr>
    </thead>
    <tbody formArrayName="tasksArr">
        <tr *ngFor="let t of tasksArr.controls; let i = index" [formGroupName]="">

        </tr>
    </tbody>
  </table>
</form>
