import { Component, Input, Output, inject } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Subject } from 'rxjs';

@Component({
  selector: 'app-thoughts',
  templateUrl: './thoughts.component.html',
  styleUrls: ['./thoughts.component.css']
})
export class ThoughtsComponent {
  @Output() onThought = new Subject<string>()
  @Input() canShare = false
  thoughtForm!: FormGroup

  fb: FormBuilder = inject(FormBuilder)


ngOnInit(): void {
    this.thoughtForm = this.fb.group({
      thought: this.fb.control('', [Validators.required, Validators.minLength(5)]),

    })

}


  clearForm() {
  this.thoughtForm.reset();
  }

  share() {
    const thought = this.thoughtForm.value['thought']
    this.onThought.next(thought)
  }

  invalid():boolean {
    return this.canShare || this.thoughtForm.invalid
  }
}


<form [formGroup]="thoughtForm" (submit)="share()">

  <div class="grid-container">
      <mat-form-field appearance="outline">
        <mat-label>My Thoughts</mat-label>
        <textarea cols="30" rows="5" type="text" matInput placeholder="Type here" formControlName="thought"></textarea>
      <mat-hint *ngIf="!canShare" class="error" align="end">
        Web share is not supported
      </mat-hint>
      </mat-form-field>
      <div>
        <button type=mat-stroked-button (click)="clearForm()">
          Clear
        </button>
        <button type="submit" mat-stroked-button [disabled]="invalid()">
        <mat-icon>share</mat-icon>
        </button>
      </div>
  </div>
  </form>
