import { Component, inject } from '@angular/core';
import { FormArray, FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-thoughts',
  templateUrl: './thoughts.component.html',
  styleUrls: ['./thoughts.component.css']
})
export class ThoughtsComponent {

  thoughtsForm!: FormGroup
  thoughtsArray!: FormArray
  fb: FormBuilder = inject(FormBuilder)


ngOnInit(): void {
    this.thoughtsForm = this.fb.group({
      thought: this.fb.control('', [Validators.required]),

    })

}


  clearForm() {
  this.thoughtsForm.reset();
  }

  addThought() {
    const thought = this.thoughtsForm.value
    this.thoughtsArray.push(thought)
  }

}
