import { Injectable } from "@angular/core";
import { HttpClient } from '@angular/common/http';
import { Observable, lastValueFrom } from "rxjs";
import { ApiResponse } from "./models";



@Injectable()
export class JokeService {
    
    http = inject
   

    getJokeAsObservable(): Observable<ApiResponse> {
        return this.http.get<ApiResponse>('https://official-joke-api.appspot.com/random_joke')
    }

    getJokeAsPromiseSetup(): Promise<string> {
        return lastValueFrom(this.http.get<ApiResponse>('https://official-joke-api.appspot.com/random_joke'))
        .then(resp => resp.setup)
    }

    getJokeAsPromisePunchline(): Promise<string> {
        return lastValueFrom(this.http.get<ApiResponse>('https://official-joke-api.appspot.com/random_joke'))
        .then(resp => resp.punchline)
    }
}
