import { Component, OnInit, inject } from '@angular/core';
import { Routes, RouterModule, Router} from '@angular/router';
import { MainComponent } from './main/main.component';
import { BearComponent } from './bear/bear.component';
import { NumberService } from './number.service';
import { NumberComponent } from './number/number.component';


const appRoutes: Routes = [
{path:'', component: MainComponent, title: 'Main'},
{path:'bear', component: BearComponent, title: 'Bear'}, 
{path: 'number', component: NumberComponent, title: 'Number'},
{path:'**', redirectTo: '/', pathMatch: 'full'},


];




@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  numberSvc = inject(NumberService);

  constructor(private router: Router) {

  }

  // give you more control to navigate 
  getABear() {
    this.router.navigate(['/bear']);
  }

  displayNumber(i : number) {
    this.numberSvc.toDisplay();
    this.router.navigate(['/number', i])


  }


}
