import { Injectable } from '@angular/core';
import { HttpClient, HttpParams } from '@angular/common/http';
import { City } from './models';



const url = "https://api.openweathermap.org/data/2.5/weather?q=Singapore&appid=d2c10dba6e897642d5f2d6346712e749";


@Injectable({
  providedIn: 'root'
})
export class WeatherService {

API_KEY = "d2c10dba6e897642d5f2d6346712e749";


  constructor(private http: HttpClient) { }


  getWeather(city: string, units='metric') {
    const params = new HttpParams()
    .set('API_KEY', this.API_KEY)
    .set('city', city);
    return this.http.get<City>(url, { params});
  }


}
