import { Component } from '@angular/core';
import { City } from '../models';




@Component({
  selector: 'app-weather',
  templateUrl: './weather.component.html',
  styleUrls: ['./weather.component.css']
})
export class WeatherComponent {
cities: City[] = [];


  addName(name: string) {
    console.warn(cityName);
    this.cities.push(name);
  }
}
<div class="container">
    <div>
<input type="text" size="30" #cityName>
<button type="button" (click)="addName(cityName.value)">Add</button> 
    </div>
    <div *ngIf="cities.length > 0; else noCity">
        <div *ngFor="let c of cities">
            <p>{{c}}</p>
        </div>
    </div>
</div>
<ng-template #noCity>
    No city
</ng-template>
