import { Injectable, inject } from '@angular/core';
import { HttpClient, HttpParams } from '@angular/common/http';
import { WeatherData } from './models';
import { Observable } from 'rxjs';
import { ActivatedRoute } from '@angular/router';

const url = '/api/weather';

//const url = "https://api.openweathermap.org/data/2.5/weather?q=Singapore&appid=d2c10dba6e897642d5f2d6346712e749";


@Injectable({
  providedIn: 'root'
})
export class WeatherService {


  http = inject(HttpClient);


  getWeather(city: string, units='metric'): Observable<WeatherData[]> {
    const params = new HttpParams()

    .set('units', units)


    return this.http.get<WeatherData[]>(`${url}`, { params});
  }


}
