import { Component, inject } from '@angular/core';
import { City } from '../models';
import { WeatherService } from '../weather.service';
import { Observable } from 'rxjs';




@Component({
  selector: 'app-weather',
  templateUrl: './weather.component.html',
  styleUrls: ['./weather.component.css']
})
export class WeatherComponent {
weather$ !: Observable<City>;

activatedRoute = inject()
weatherSvc = inject(WeatherService);

  addName(name: string) {
    console.warn(name);
    this.weather$ = this.weatherSvc.getWeather(name);

  }
}
<div class="container">
    <div>
<input type="text" size="30" #cityName>
<button type="button" (click)="addName(cityName.value)">Add</button> 
    </div>
    <div *ngIf="weather$ | async as w; else noCity">
            <a>{{w.name}}</a>
    </div>
</div>
<ng-template #noCity>
    No city
</ng-template>
