import { Component, OnInit, inject } from '@angular/core';
import { Observable } from 'rxjs';
import { UploadService } from '../upload.service';

@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent implements OnInit{

  images$!: Observable<any>
  uploadSvc = inject(UploadService)


  ngOnInit(): void {
      this.images$ = this.uploadSvc.getImages()
      getImage() {
        const key = this.imageKey.nativeElement.value
        this.uploadSvc.getImage(key).subscribe(
          value => {
            this.createImage(value);
          }
        )
      }
      }
  }

  private createImage(image: Blob) {
    if (image && image.size > 0) {
      let reader = new FileReader();
      reader.onload = (e) => this.picture = e.target?.result;
      reader.readAsDataURL(image);
    }
  }

}
<div>
    <table>
        <tr>
            <td>List of Images</td>
            <td>
                <ul *ngIf="images$ | async as i; else noImages">
                    <a href="http://localhost:8080/{{i}}"><li>{{i}}</li></a>
                </ul>
            </td>
        </tr>
    </table>
</div>
<ng-template #noImages>
    no images
</ng-template>
