<div>
    <table>
        <tr>
            <td>Upload more image</td>
            <td>
                <button [routerLink]="['/']">Upload</button>
            </td>
        </tr>
        <tr *ngIf="images$; else noImages">
            <td>List of Images</td>
            <td *ngIf="images$ | async as image">
                <ul *ngFor="let i of image.keys">
                    <a [routerLink]="['/detail', i]"><li>{{i}}</li></a>
                </ul>
            </td>
        </tr>
        <tr>
            <ng-template #noImages>
                no images
            </ng-template>
        </tr>
    </table>
</div>
