import { Component, ElementRef, OnInit, ViewChild, inject } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { UploadService } from './upload.service';
import { Observable, firstValueFrom } from 'rxjs';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit{
  @ViewChild('uploadFile')
  uploadFile!: ElementRef
  @ViewChild('imageKey')
  imageKey!: ElementRef


  image = ''

  form!: FormGroup
  fb = inject(FormBuilder)
  uploadSvc = inject(UploadService)

  ngOnInit(): void {
      this.form = this.fb.group({
        title: this.fb.control<string>('', [Validators.required]),
        file: this.fb.control<File | null>(null, [Validators.required])
      })
  }

  upload() {
    const f: File = this.uploadFile.nativeElement.files[0]
    const data = this.form.value
    console.info('data: ', data)
    console.info('file: ', f)

    firstValueFrom(this.uploadSvc.upload(data['title'], f))
    .then(result => {
      alert('uploaded')
      this.form.reset()
    })
    .catch(err => {
      alert(JSON.stringify(err))
    })
  }

  getImage() {
    const key = this.imageKey.nativeElement.value
    this.uploadSvc.getImage(key).subscribe(
      value => {
        this.createImage(value);
      }

    )
  }

  private createImage(image: Blob) {
    if (image && image.size > 0) {
      let reader = new FileReader();
      reader.onload = (e) 
      reader.readAsDataURL(image);
    }
  }
}
