import { Component, OnInit, ViewChild, inject } from '@angular/core';
import { CameraService } from '../camera.service';
import { Router } from '@angular/router';
import { Buffer } from 'buffer';



@Component({
  selector: 'app-upload',
  templateUrl: './upload.component.html',
  styleUrls: ['./upload.component.css']
})
export class UploadComponent implements OnInit {


  @ViewChild('comment')
private comments: string = '';


  photo = ""

  cameraSvc = inject(CameraService)
  router = inject(Router)

  ngOnInit() {
    if (!this.cameraSvc.photo) {
      this.router.navigate(['/'])
      return
    }

    this.photo = this.cameraSvc.photo
    this.dataUrlToFile(this.photo, comments:)

  }

  dataUrlToFile(dataUrl: string, filename: string): File | undefined {
    const arr = dataUrl.split(',');
    if (arr.length < 2) { return undefined; }
    const mimeArr = arr[0].match(/:(.*?);/);
    if (!mimeArr || mimeArr.length < 2) { return undefined; }
    const mime = mimeArr[1];
    const buff = Buffer.from(arr[1], 'base64');
    return new File([buff], filename, {type:mime});
  }

}
